package Loops;

public class displayhelloworld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter = 0;
		int repeat = 0;
		for (repeat = 0; repeat <= 10; repeat++) {
			for (counter = 0; counter < 5; counter++) {
				System.out.println(repeat + " Hello World!");

			}

		}
	}

}
