package Loops;

public class forloopsdisplaythesumofnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter = 0;
		int sum = 0;
		for (counter = 1; counter <= 10; counter++) {
			sum = sum + counter;
		}
		System.out.println(sum);
	}

}
